package com.example.rapportv2;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.firebase.ui.firestore.paging.FirestorePagingAdapter;import com.firebase.ui.firestore.paging.FirestorePagingOptions;import com.squareup.picasso.Picasso;public class PostAdapter extends FirestorePagingAdapter<Posts, PostAdapter.PostHolder>{    public PostAdapter(@NonNull FirestorePagingOptions<Posts> options) {        super(options);    }    @Override    protected void onBindViewHolder(@NonNull PostHolder holder, int position, @NonNull Posts model) {        holder.textViewUsername.setText("\uD83D\uDC68\u200Dâš• "+model.getUsername());        holder.textViewDescription.setText(model.getDescription());        holder.relativeLayoutPostImageBox.setVisibility(View.VISIBLE);            Picasso.get().load(model.getPostimage())                    .placeholder(R.mipmap.ic_launcher)                    .fit()                    .centerCrop()                    .into(holder.imageViewPostImage);             if( model.getPostimage() == null )             {                 holder.relativeLayoutPostImageBox.setVisibility(View.GONE);             }    }    @NonNull    @Override    public PostHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.all_post_layout,parent,false);        return new PostHolder(v);    }    class PostHolder extends RecyclerView.ViewHolder{        TextView textViewUsername;        TextView textViewDescription;        ImageView imageViewPostImage;        RelativeLayout relativeLayoutPostImageBox;        public PostHolder(@NonNull View itemView) {            super(itemView);            textViewUsername = itemView.findViewById(R.id.post_username);            textViewDescription = itemView.findViewById(R.id.post_description);            imageViewPostImage = itemView.findViewById(R.id.post_image);            relativeLayoutPostImageBox = itemView.findViewById(R.id.post_image_box);        }    }}